import * as React from "react";
//import { NavLink } from "react-router-dom";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import Badge from "react-bootstrap/Badge";
//import Button from "react-bootstrap/Button";
//import Backbutton from "../components/Backbutton";

import "../assets/css/DetallePokemon.css";

export default function DetallePokemones() {
  const [datospoke, setDatospoke] = useState([]);
  const [img, setImg] = useState("");
  const [tipo, setTipo] = useState([]);
  const { name } = useParams();

  const consultarPokemon = async () => {
    const endpoint = "https://pokeapi.co/api/v2/pokemon/";
    const direccion = `${endpoint}${name}`;
    const response = await fetch(direccion);
    const data = await response.json();
    const getDatos = data.stats;
    setDatospoke(getDatos);
    setImg(data.sprites.other.dream_world);
    setTipo(data.types);
    console.log(data.types);
  };

  useEffect(() => {
    consultarPokemon();
  }, []);

  return (
    <div>
      <div className="pokecards"  >
        <Card
          className="row justify-content-evenly gap shadow p-3 mb-90 bg-secondary rounded"
          id="pokecard"
          bg="secondary"
          style={{ width: "30rem", shadow:"10px 10px 5px lightblue" }}
        >
          <div class="col-md-12 text-center">
            <Card.Img
              variant="top"
              style={{ width: "40%" }}
              src={img.front_default}
            />
          </div>
          <Card.Body>
            <Card.Title
              style={{
                textAlign: "center",
                color: "whitesmoke",
                textTransform: "uppercase",
              }}
            >
              {" "}
              <h1> {name} </h1>{" "}
            </Card.Title>
            <Card.Text style={{ color: "whitesmoke" }}>
              {datospoke.map((e) => (
                <ListGroup as="ul" /* className="list-group-flush" */>
                  <ListGroup.Item style={{ color: "black" }} key={e.name}>
                    {e.stat.name}: {e.base_stat}
                  </ListGroup.Item>
                </ListGroup>
              ))}
              {tipo.map((t) => (
                <h3 className="text-center">
                  <ol>
                    <Badge bg="danger">Tipo: {t.type.name} </Badge>
                  </ol>
                </h3>
              ))}
            </Card.Text>

            {/*           <div class="col-md-12 text-center">
            <Button variant="danger">Volver al inicio</Button>
          </div> */}
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}
